
public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// generación de las variables solicitadas por el ejercicio 1
		double temperatura = 23.2; // Temperatura de la habitación
		
		String mascota = "Kenzo"; // Nombre de mi mascota
		
		boolean par = true; // booleano que indica si par o impar
		
		final double pi = 3.14159; // Pi con 5 decimales
		
		float saldo = 1300.20f; // Saldo de una cuenta bancaria
		
		String numeroSerie = "A3BN2S"; // Número de serie de un dispositivo
		
		// En esta parte imprimimos todos los datos por la consola.
		
		System.out.println("La temperatura es: " + temperatura);
		
		System.out.println("El nombre de mi mascota es: " + mascota);
		
		System.out.println("¿El número es par?: " + par);
		
		System.out.println("El valor de PI es: " + pi);
		
		System.out.println("El saldo de la cuenta es: " + saldo);
		
		System.out.println("El número de serie es: " + numeroSerie);
		
		
	}

}
